import Web3 from 'web3';
console.clear();
const web3 = new Web3(Web3.givenProvider);

async function interactWithContract() {

    const accounts = await web3.eth.getAccounts();
    console.log(accounts);

    const address = "0xbF48F95FEB98eebd108B1Eb0A49eF39f06612372";
    const abi = [
	{
		"inputs": [],
		"stateMutability": "nonpayable",
		"type": "constructor"
	},
	{
		"inputs": [],
		"name": "getHelloMessage",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "helloMessage",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	}
];
    const contract = new web3.eth.Contract(abi, address);

    const value = await contract.methods.getHelloMessage().call(); 
    console.log(value);
}
function App() {
    if (window.ethereum) { 
        window.ethereum
        .request({ method: "eth_requestAccounts" }) 
        .then((res) => console.log(res));
        interactWithContract();
    }
    else {
        alert("install metamask extension !!");
    }
    return (
        <div style={{ fontFamily: 'Arial, sans-serif', margin: 0, padding: 0, backgroundColor: '#f0f0f0' }}>
            <h2 style={{ color: '#333', textAlign: 'center', marginTop: '50px' }}>TrusTech User Interface</h2>

            <form action="/submit_product_info" style={{ width: '300px', margin: '30px auto', backgroundColor: '#fff', padding: '20px', border: '1px solid #ccc', boxShadow: '0 0 10px #ccc' }}>
                <label htmlFor="img">Insert Image:</label><br />
                <input type="file" id="img" name="img" accept="image/*" /><br /><br />

                <label htmlFor="pname">Product Name:</label><br />
                <input type="text" id="pname" name="pname" /><br /><br />

                <label htmlFor="pserial">Product Serial Number:</label><br />
                <input type="text" id="pserial" name="pserial" /><br /><br />

                <label htmlFor="mac">MAC Address:</label><br />
                <input type="text" id="mac" name="mac" /><br /><br />

                <label htmlFor="mserial">Motherboard Serial Number:</label><br />
                <input type="text" id="mserial" name="mserial" /><br /><br />

                <input type="submit" value="Register Product" />
            </form>
        </div>
    );
}

export default App;
