import Web3 from 'web3';
console.clear();
const web3 = new Web3(Web3.givenProvider);

async function interactWithContract() {

    const accounts = await web3.eth.getAccounts();
    console.log(accounts);

    const address = "0x587dbc8cf23a05a87d3DdAeB16a51478196fa845";
    const abi = [
	{
		"inputs": [],
		"stateMutability": "nonpayable",
		"type": "constructor"
	},
	{
		"inputs": [],
		"name": "getHelloMessage",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "helloMessage",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	}
];
    const contract = new web3.eth.Contract(abi, address);

    const value = await contract.methods.getHelloMessage(); 
    console.log(value);
}
function App() {
    if (window.ethereum) {
        window.ethereum
        .request({ method: "eth_requestAccounts" }) 
        .then((res) => console.log(res));
        interactWithContract();
    }
    else {
        alert("install metamask extension!!");
    }
    return (
        <div className="App">
            Look at console !
        </div>
    );
}

export default App;
