import Web3 from 'web3';
console.clear();

const web3 = new Web3(Web3.givenProvider);

async function interactWithContract() {
  const accounts = await web3.eth.getAccounts();
  console.log(accounts);

  const address = "0x97C663fbf86f09C582eF2e097227d34fD312dB78";
  const abi = [
	{
		"inputs": [],
		"name": "retrieve",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "nonpayable",
		"type": "function"
	},
	{
		"inputs": [
			{
				"internalType": "string",
				"name": "newData",
				"type": "string"
			}
		],
		"name": "store",
		"outputs": [],
		"stateMutability": "nonpayable",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "storedData",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	}
];
  const contract = new web3.eth.Contract(abi, address);

  var value = await contract.methods.retrieve().call();
  console.log(value);

  await contract.methods.store(2).send({ from: accounts[0] } );
}

function App() {
  if (window.ethereum) {
    window.ethereum
    .request({ method: "eth_requestAccounts" })
    .then( (res) => console.log(res) );
    interactWithContract();
  } else {
    alert("install metamask extension!!");
  }
  return (
    <div className="App">
Look at the console! (F12)
    </div>
  );
}

export default App;
