import Web3 from 'web3';
import './App.css';

console.clear();
const web3 = new Web3(Web3.givenProvider);

async function interactWithContract() {

    const accounts = await web3.eth.getAccounts();
    console.log(accounts);

    const address = "0xbF48F95FEB98eebd108B1Eb0A49eF39f06612372";
    const abi = [
	{
		"inputs": [],
		"stateMutability": "nonpayable",
		"type": "constructor"
	},
	{
		"inputs": [],
		"name": "getHelloMessage",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "helloMessage",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	}
];
    const contract = new web3.eth.Contract(abi, address);

    const value = await contract.methods.getHelloMessage().call(); 
    console.log(value);
}

function previewImage() {
    let reader = new FileReader();
    reader.onload = function(e) {
      document.getElementById('preview').src = e.target.result;
      document.getElementById('preview').style.display = 'block';
    };
    reader.readAsDataURL(document.getElementById('img').files[0]);
  }

  function handleSubmit(e) {
    e.preventDefault();

    // Get the form data
    // let productName = document.getElementById('pname').value;
